mapper.update:
update base_msg set status = 0
where unique_id in

    #{uniqueId}

update base_msg set status = 1
where from_user_id = #{fromUserId}
  and to_user_id = #{toUserId}
  and status = 0;

mapper.select:
select unique_id from base_msg
 where from_user_id = #{fromUserId}
   and to_user_id = #{toUserId}
   and 'status' = 0
